#!/usr/bin/env perl

use Modern::Perl;
use File::Slurp;

my @lines = read_file( \*DATA );

say scalar(@lines);
my @colnames = split('\t', shift(@lines));
shift(@colnames);
my @rownames;
my @data;

foreach my $line (@lines) {
    my @p = split('\t', $line);
    push @rownames, shift(@p);
    my @val;
    map { push @val, sprintf('%f', $_) } @p[0..$#p];
    push @data, [ @val ];
}



